// This allows TypeScript to pick up the magic constants that's auto-generated by Forge's Webpack
// plugin that tells the Electron app where to look for the Webpack-bundled app code (depending on
// whether you're running in development or production).
declare const MAIN_WINDOW_WEBPACK_ENTRY: string;
declare const MAIN_WINDOW_PRELOAD_WEBPACK_ENTRY: string;

// Get the path to the generated_assets directory
// When packaged: process.resourcesPath points to the resources directory
// When in development: use app.getAppPath() to get the project root
function getAssetsPath(): string {
  if (app.isPackaged) {
    // In production, extraResource files are in process.resourcesPath
    return path.join(process.resourcesPath, 'generated_assets');
  } else {
    // In development, use app.getAppPath() to get the application root
    const appPath = app.getAppPath();
    return path.join(appPath, 'generated_assets');
  }
}

function getAssetPath(...paths: string[]): string {
  return path.join(process.resourcesPath, ...paths);
};

