/**
 * - A function to create a pluto window.
 * - It checks whether called from CLI or GUI.
 * - It checks if a pluto instance is already running.
 * - Installs dev extensions if needed
 * - Takes care of app updates
 *
 * *NOTE:* If any param is `unknown` it is overwritten by the CLI options,
 * if possible.
 *
 * @param url the URL to open in this window
 * @param project the project folder location
 * @param notebook the path to a pluto notebook
 * @returns nothing
 */
const createWindow = (): void => {
  // Determine preload script path
  let preloadPath: string | undefined;
  
  if (fs.existsSync(MAIN_WINDOW_PRELOAD_WEBPACK_ENTRY)) {
    preloadPath = MAIN_WINDOW_PRELOAD_WEBPACK_ENTRY;
  } else {
    // In development, try the .webpack directory directly
    const devPreloadPath = path.join(app.getAppPath(), '.webpack/renderer/main_window/preload.js');
    if (fs.existsSync(devPreloadPath)) {
      preloadPath = devPreloadPath;
    }
  }

  const win = new BrowserWindow({
      title: '⚡ Pluto ⚡',
      height: 800,
      width: 700,
      resizable: true,
      show: true,
      backgroundColor: nativeTheme.shouldUseDarkColors ? '#1F1F1F' : 'white',
      icon: getAssetPath('icon.png'),
      webPreferences: {
        preload: MAIN_WINDOW_PRELOAD_WEBPACK_ENTRY,
      },
    });
    
    const firstPluto = new Pluto(win);
    // const window = firstPluto.getBrowserWindow();
    win.focus();

    win.webContents.setVisualZoomLevelLimits(1, 3);
    win.setMenuBarVisibility(false);  
};

// This method will be called when Electron has finished
// initialization and is ready to create browser windows.
// Some APIs can only be used after this event occurs.
app.whenReady().then(async () => {
// app.on('ready', async () => {

// Read the architecture file generated by generateAssets
  // const arch = readArchitectureFile();
  // if (arch) {
  //   console.log(`Running on architecture: ${arch}`);
  // }
  await initGlobals();
  createWindow();
  startup(app);

  // This is set here because it required the app to be ready
  session.defaultSession.on('will-download', (_event, item) => {
      const fileName = item.getFilename();
      const ext = fileName.split('.')[fileName.split('.').length - 1];
      let fileType = 'Pluto Statefile';
      if (ext.endsWith('html')) fileType = 'HTML file';
      else if (ext.endsWith('jl')) fileType = 'Pluto Notebook';

      item.setSaveDialogOptions({
        message: 'Select location to export file',
        filters: [{ extensions: [ext], name: fileType }],
      });
  });

  createRequestListener();
});

// Quit when all windows are closed, except on macOS. There, it's common
// for applications and their menu bar to stay active until the user quits
// explicitly with Cmd + Q.
app.on('window-all-closed', () => {
  if (process.platform !== 'darwin') {
    app.quit();
  }
});

app.on('activate', async () => {
  // On OS X it's common to re-create a window in the app when the
  // dock icon is clicked and there are no other windows open.
  if (BrowserWindow.getAllWindows().length === 0) {
    createWindow();
  }
});

